.code-viewer-backdrop {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  animation: fadeIn 0.2s ease-in-out;
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.code-viewer-container {
  background: white;
  border-radius: 8px;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.3);
  width: 90%;
  max-width: 1000px;
  height: 80vh;
  display: flex;
  flex-direction: column;
  overflow: hidden;
  animation: slideUp 0.3s ease-out;
}

@keyframes slideUp {
  from {
    transform: translateY(20px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

.code-viewer-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 12px 16px;
  border-bottom: 1px solid #e0e0e0;
  background-color: #f5f5f5;
  gap: 12px;
}

.code-tab {
  display: flex;
  gap: 8px;
  flex-wrap: wrap;
  flex: 1;
  align-items: center;
}

.code-tab-item {
  padding: 8px 16px;
  background: white;
  border: 1px solid #ddd;
  border-radius: 4px 4px 0 0;
  cursor: pointer;
  font-size: 13px;
  font-family: 'Courier New', monospace;
  transition: all 0.2s ease;
  border-bottom: 2px solid transparent;
}

.code-tab-item:hover {
  background-color: #fafafa;
  border-color: #999;
}

.code-tab-item.active {
  background: white;
  border-color: #333;
  border-bottom-color: #2563eb;
  font-weight: 500;
}

.close-button {
  background: none;
  border: none;
  font-size: 24px;
  cursor: pointer;
  color: #666;
  padding: 4px 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: color 0.2s ease;
  flex-shrink: 0;
}

.close-button:hover {
  color: #000;
}

.code-content {
  flex: 1;
  overflow: auto;
  background: #f8f8f8;
  padding: 16px;
}

.code-lines {
  margin: 0;
  padding: 12px;
  background: white;
  border-radius: 4px;
  border: 1px solid #ddd;
  font-family: 'Courier New', monospace;
  font-size: 13px;
  line-height: 1.5;
  color: #333;
  overflow-x: auto;
}

.code-lines code {
  font-family: 'Courier New', monospace;
}

.no-file-selected {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
  color: #999;
  font-size: 14px;
}

.loading {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
  color: #666;
  font-size: 14px;
}

@media (max-width: 768px) {
  .code-viewer-container {
    width: 95%;
    height: 90vh;
  }

  .code-tab {
    flex-wrap: nowrap;
    overflow-x: auto;
  }

  .code-lines {
    font-size: 12px;
    padding: 8px;
  }
}

components/code-viewer.code-viewer.html:
@if (isOpen) {
  <section class="code-viewer-backdrop" (click)="close()">
    <section class="code-viewer-container" (click)="$event.stopPropagation()">
      <div class="code-viewer-header">
        <div class="code-tab">
          @for (fileName of fileNames; track fileName) {
            <button
              class="code-tab-item"
              [class.active]="selectedFileName === fileName"
              (click)="selectFile(fileName)"
            >
              {{ fileName }}
            </button>
          }
        </div>
        <button class="close-button" (click)="close()" title="Close">
          âœ•
        </button>
      </div>

      <section class="code-content">
        @if (isLoading) {
          <div class="loading">Loading files...</div>
        } @else if (selectedFileContent) {
          <pre class="code-lines"><code>{{ selectedFileContent }}</code></pre>
        } @else {
          <div class="no-file-selected">No files found for: {{ componentPath }}</div>
        }
      </section>
    </section>
  </section>
}

components/code-viewer.code-viewer.ts:
import { Component, OnInit, Input, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ComponentFileService, ComponentFiles } from './component-file.service';

@Component({
  selector: 'app-code-viewer',
  imports: [CommonModule],
  standalone: true,
  templateUrl: './code-viewer.html',
  styleUrl: './code-viewer.css',
})
export class CodeViewer implements OnInit {
  private fileService = inject(ComponentFileService);

  isOpen = false;
  files: ComponentFiles = {};
  selectedFileName = '';
  selectedFileContent = '';
  fileNames: string[] = [];
  isLoading = false;

  @Input() componentPath: string = '';

  async ngOnInit() {
    // Auto-load component files from the current component's directory
    if (this.componentPath) {
      await this.loadFiles();
    }
  }

  async loadFiles() {
    this.isLoading = true;
    this.files = await this.fileService.loadComponentFiles(this.componentPath);
    this.fileNames = Object.keys(this.files).sort();

    // Auto-select first file if available
    if (this.fileNames.length > 0) {
      this.selectFile(this.fileNames[0]);
    }

    this.isLoading = false;
  }

  selectFile(fileName: string) {
    this.selectedFileName = fileName;
    this.selectedFileContent = this.files[fileName] || '';
  }

  open() {
    this.isOpen = true;
  }

  close() {
    this.isOpen = false;
  }
}

components/code-viewer.component-file.service.ts:
import { Injectable, inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

export interface ComponentFiles {
  [filename: string]: string;
}

interface ComponentManifest {
  [componentPath: string]: ComponentFiles;
}

@Injectable({
  providedIn: 'root'
})
export class ComponentFileService {
  private http = inject(HttpClient);
  private manifest: ComponentManifest | null = null;
  private manifestPromise: Promise<ComponentManifest> | null = null;

  /**
   * Load component files from the pre-generated manifest
   * The manifest is built at compile time with all component source files
   * @param componentPath - Path to component (e.g., 'michael/starwars-info')
   */
  async loadComponentFiles(componentPath: string): Promise<ComponentFiles> {
    if (!this.manifest && !this.manifestPromise) {
      this.manifestPromise = this.loadManifest();
    }

    try {
      const manifest = await (this.manifestPromise || this.manifest);
      if (!manifest) {
        console.warn('No manifest available');
        return {};
      }

      // Check if component exists in manifest
      if (manifest[componentPath]) {
        return manifest[componentPath];
      }

      console.warn(`Component path not found in manifest: ${componentPath}`);
      return {};
    } catch (error) {
      console.error(`Error loading component files for ${componentPath}:`, error);
      return {};
    }
  }

  private async loadManifest(): Promise<ComponentManifest> {
    if (this.manifest) {
      return this.manifest;
    }

    try {
      const result = await this.http
        .get<ComponentManifest>('/assets/component-manifest.json')
        .toPromise();

      this.manifest = result || {};

      return this.manifest;
    } catch (error) {
      console.error('Error loading component manifest:', error);
      return {};
    }
  }
}

